        function numshow(e) {
            var stv1;
            var x = parseInt($(e).attr("alt"));
            var y = 0;
            x = "" + x;
            var arr = [];
            for (var i = 0; i < x.length; i++) {
                var elem = $("<i></i>");
                arr[i] = elem.text(x.charAt(i));
            }
            if (x >= 100) {
                var z = parseInt(x / 100);
                var t = 10;
            } else {
                var z = 1;
                t = 150;
            }
            var stv1 = setInterval(function () {
                y = y + z;
                $(e).text(y);
                if (y + z >= x) {
                    /*$(e).text(x);*/
                    $(e).html("");
                    for (var i = 0; i < arr.length; i++) {
                        arr[i].appendTo(e);
                    }
                    clearInterval(stv1);
                }
            }, t);
        }
