        function getAccount() {
            $.ajax({
                url: '/coin/account/info',
                type: 'GET',
                cache: false,
                success: function (data) {
                    if (data.status !== 500) {
                        if (data.data) {
                            $('#my_coin').html(data.data.account.amount).attr('alt', data.data.account.amount);
                            numshow("#my_coin");
                        }
                    } else {
